def process_boolean(p1, p2, op='AND'):
    if op == 'AND':
        return sorted(list(set(p1) & set(p2)))
    elif op == 'OR':
        return sorted(list(set(p1) | set(p2)))
    elif op == 'NOT':
        return sorted(list(set(p1) - set(p2)))

postings_apple = [1, 3, 5, 8]
postings_pie = [2, 3, 5, 7]

result = process_boolean(postings_apple, postings_pie, 'AND')
print(f"APPLE AND PIE: {result}")
